let report w =
  match is_active w with
  | false -> `Inactive
  | true ->
      has_warnings := true;
      if is_error w then incr nerrors;
      `Active
        {
          number = number w;
          message = message w;
          is_error = is_error w;
          sub_locs = sub_locs w;
        }

exception Errors

let reset_fatal () = nerrors := 0

let check_fatal () =
  if !nerrors > 0 then (
    nerrors := 0;
    raise Errors)

let descriptions =
  [
