  match x with 
  | Nolabel -> y = Nolabel
  | Labelled s ->
    begin match y with 
    | Labelled s0 -> s = s0 
    | _ -> false 
    end 
  | Optional s ->
      begin match y with 
      | Optional s0 -> s = s0
      | _ -> false  
      end  
