    would not raise
*)

val non_exn_protect : 'a ref -> 'a -> (unit -> 'b) -> 'b

val protect : 'a ref -> 'a -> (unit -> 'b) -> 'b

val protect2 : 'a ref -> 'b ref -> 'a -> 'b -> (unit -> 'c) -> 'c

